<?phpclass Archivo {    public static function Subir() {        		//IMPLEMENTAR...    }    public static function Borrar($path) {		//IMPLEMENTAR...		return unlink($path);    }    public static function Mover($pathOrigen, $pathDestino) {		//IMPLEMENTAR...		if(!file_exists($pathOrigen) || file_exists($pathDestino)){			return false;		}		if(copy($pathOrigen, $pathDestino)){			return Archivo::Borrar($pathOrigen);		}		return false;    }}